INSERT INTO `type` (`ID`,`name`,`schema`,`template`,`dateCreated`,`dateDeleted`,`client`) VALUES (5,'quote','{\n	\"$schema\": \"http://json-schema.org/draft-07/schema#\",\n	\"description\": \"Blockquote\",\n	\"type\": \"object\",\n	\"properties\": {\n		\"quote\": {\n			\"type\": \"string\",\n			\"description\": \"The quote itself.\",\n			\"minLength\": 1\n		},\n		\"url\": {\n			\"type\": \"string\",\n			\"format\": \"uri\",\n			\"description\": \"The Cited article\'s url.\",\n			\"minLength\": 1\n		},\n		\"source\": {\n			\"type\": \"source\",\n			\"description\": \"The original cited source\'s name.\",\n			\"minLength\": 1,\n			\"maxLength\": 255\n		},		\n		\"author\": {\n			\"type\": \"author\",\n			\"description\": \"The original author.\",\n			\"minLength\": 1,\n			\"maxLength\": 255\n		},\n		\"published\": {\n			\"type\": \"date\",\n			\"description\": \"The original publication date.\",\n			\"minLength\": 1,\n			\"maxLength\": 255\n		}		\n	},\n	\"required\": [\"quote\"]\n}','<blockquote\n  {{ ifpresent [url] }} cite=\"[url]\" {{ endif }}\n  >\n  <p>[quote]</p>\n  {{ ifpresent [author] }}\n    <footer>\n      â€”[author],\n      {{ ifpresent [source] }}, <cite>[source]</cite>{{ endif }}\n      {{ ifpresent [published] }}, <time>[published]</time>{{ endif }}\n    </footer>{{ endif }}\n</blockquote>','2020-01-1 01:01:01','1000-01-01 00:00:00',NULL);
